import { Component, Input, OnInit } from '@angular/core';
import { UsersService } from '../service/users.service';

@Component({
  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['./user.component.scss'],

})
export class UserComponent implements OnInit  {
  @Input() name = '';
  @Input() status = '';
  @Input() img = '';
  groupName = 'Healthy lifestyle';
  groupNameInput = '';
  newName: string = ''; 
  newStatus: string = '';

  constructor(public usersService: UsersService) {}
  ngOnInit(): void {

  }
  editName(newName: string): void {
    this.newName = newName;
  }

  editStatus(newStatus: string): void {
    this.newStatus  = newStatus;
  }

  editGroupName(): void {
    this.usersService.updateGroupName(this.groupNameInput);
    this.groupName = this.groupNameInput;
  }
  onNameEdit(): void {
    this.name = this.newName;
    this.newName = '';
  }

  onStatusEdit(): void {
    this.status = this.newStatus;
    this.newStatus = '';
  }
}
<div class="wrapper">
    <div class="body">
        <div class="picture">
            <img [src]="img" alt="">
        </div>
        <div class="user-data">
            <div class="user-data-item">
                <h2>{{name}}</h2>
                <div class="edit-body">
                    <input [(ngModel)]="newName" type="text" placeholder="Write new name">
                    <button (click)="onNameEdit()" class="network">Edit</button>
                </div>
            </div>
            <div class="user-data-item">
                <div class="status-text">{{status}}</div>
                <div class="edit-body">
                    <input [(ngModel)]="newStatus" type="text" placeholder="Write new status">
                    <button (click)="onStatusEdit()" class="network">Edit</button>
                </div>
            </div>
        </div>
    </div>
    <div class="group">
        <p><b>Group Name:</b><span>{{usersService.getGroupName()}}</span></p>
    </div>
</div>>
