import { Component, ViewChild, ViewContainerRef, ComponentRef} from '@angular/core';
import { UsersService }from './service/users.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
  providers: [UsersService]
})
export class AppComponent {

  globalGroupName = '';
  globalGroupNameInput = '';

  @ViewChild('popUp', { read: ViewContainerRef })
  private viewRef!: ViewContainerRef
  private componentRef!: ComponentRef<UsersService>

  constructor(public usersService: UsersService){}

  ngOnInit(): void {
    this.globalGroupName = this.usersService.getGroupName();
  }
  editGroupName(): void {
    this.usersService.updateGroupName(this.globalGroupNameInput);
    this.globalGroupName = this.usersService.getGroupName();
  }
  showPopUp(){
    this.viewRef.createComponent(UsersService)
  }
}
