import { Component } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent {

}
<div class="body">
    <h1> <b>User group: </b><span>{{globalGroupName}}</span></h1>
    <div class="edit-body">
        <input [(ngModel)]="globalSGroupNameInput" type="text" placeholder="Write new group name">
        <button class="network">Edit</button>
    </div>
</div>
