import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['./user.component.scss'],

})
export class UserComponent {
  @Input() name = '';
  @Input() status = '';
  @Input() img = '';
}
<div class="wrapper">
    <div class="body">
        <div class="picture">
            <img [src]="img" alt="">
        </div>
        <div class="user-data">
            <div class="user-data-item">
                <h2>{{name}}</h2>
                <div class="edit-body">
                    <input type="text" placeholder="Write new name">
                    <button class="network">Edit</button>
                </div>
            </div>
            <div class="user-data-item">
                <div class="status-text">{{userComponent.status}}</div>
                <div class="edit-body">
                    <input type="text" placeholder="Write new status">
                    <button class="network">Edit</button>
                </div>
            </div>
        </div>
    </div>
<div class="group">
    <p><b>Group Name:</b><span> Healthy lifestyle</span></p>
</div>
</div>
