import { Component } from '@angular/core';
import { UsersService } from '../service/users.service'

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent {
  groupName: string = 'Healthy lifestyle'; 
  groupNameInput: string = ''; 

  constructor(public usersService: UsersService){}

  editGroupName(): void {
    this.usersService.updateGroupName(this.groupNameInput);
    this.groupName = this.groupNameInput;
  }
}
<div class="body">
    <h1> <b>User group: </b><span>{{groupName}}</span></h1>
    <div class="edit-body">
        <input [(ngModel)]="groupNameInput" type="text" placeholder="Write new group name">
        <button class="network" (click)="editGroupName()">Edit</button>
    </div>
</div>
