import { Component, ComponentFactoryResolver, ViewContainerRef } from '@angular/core';
import { PopUpComponent } from './pop-up/pop-up.component';
import { UsersService } from './service/users.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  private viewRef: any;

  constructor(private componentFactoryResolver: ComponentFactoryResolver, private viewContainerRef: ViewContainerRef, private usersService: UsersService) {}

  getUsers() {
    return this.usersService.getUsers();
  }

  showPopUp() {
    this.openPopUp();
  }

  private openPopUp() {
    const factory = this.componentFactoryResolver.resolveComponentFactory(PopUpComponent);
    this.viewRef = factory.create(this.viewContainerRef.injector);
    this.viewRef.instance.members = this.usersService.getUsers().length;
    this.viewRef.instance.name = this.usersService.getGroupName();
    this.viewRef.instance.close.subscribe(() => {
      this.closePopUp();
    });
    this.viewContainerRef.insert(this.viewRef.hostView);
  }

  private closePopUp() {
    this.viewRef.destroy();
  }
}
